<?php global $post;$post_slug = $post->post_name;if($post_slug == 'login'){    include "signin.php";    exit();}$url = get_assets_url();$pid = get_the_ID();$feat_image = wp_get_attachment_url( get_post_thumbnail_id($pid) );?><?php get_header('home'); ?><link href="<?= $url; ?>css/submit-rider.css" rel="stylesheet"><style>    @media screen and (max-width: 782px)    {html {    margin-top: 0px !important;}}html {    margin-top: 0px !important;}</style>	<main>		<div class="hero_single inner_pages background-image" data-background="url(<?= $feat_image ?>)">			<div class="opacity-mask" data-opacity-mask="rgba(0, 0, 0, 0.6)">				<div class="container">					<div class="row justify-content-center">						<div class="col-xl-9 col-lg-10 col-md-8">							<h1 style="visibility: visible;"><?= get_the_title(); ?></h1>						</div>					</div>					<!-- /row -->				</div>			</div>		</div>		<!-- /hero_single -->		<div class="bg_gray container"><?php    // TO SHOW THE PAGE CONTENTS    while ( have_posts() ) : the_post(); ?> <!--Because the_content() works only inside a WP Loop -->        <div class="entry-content-page">            <?php the_content(); ?> <!-- Page Content -->        </div><!-- .entry-content-page -->    <?php    endwhile; //resetting the page loop    wp_reset_query(); //resetting the page query    ?></div>		<!-- /bg_gray -->			</main>	<!-- /main --><?php get_footer('home'); ?> 