<?phpfunction get_times( $default = '19:00', $interval = '+30 minutes' ) {    $output = '';    $current = strtotime( '00:00' );    $end = strtotime( '12:00' );    while( $current <= $end ) {        $time = date( 'H:i', $current );        $sel = ( $time == $default ) ? ' selected' : '';        $output .= "<option value=\"{$time}\"{$sel}>" . date( 'h.i A', $current ) .'</option>';        $current = strtotime( $interval, $current );    }    return $output;}?><?php$days = array();$days[] = array(    "name" => "Monday"    );$days[] = array(    "name" => "Tuesday"    );    $days[] = array(    "name" => "Wednesday"    );    $days[] = array(    "name" => "Thursday"    );    $days[] = array(    "name" => "Firday"    );    $days[] = array(    "name" => "Saturday"    );    $days[] = array(    "name" => "Sunday"    );    ?><h3>Service Provider Package detail</h3><br><span>Only for service provider</span>	<table class="form-table">        <tr>            <th>Verified acount</th>            <td><input type="checkbox" name="varified" <?= (get_user_meta($user->ID, 'varified',true)?"checked":"") ?>></td>        </tr>        <tr>          <td>Products</td>          <td></td>        </tr>        <?php        $ucats = get_user_meta($user->ID,'scats',true);        $ucats = explode(',',$ucats);        $all_categories = all_cats();                foreach($all_categories as $cat) : ?>        <?php        ?>        <tr>          <th><?php echo $cat->name ?> Product</th>          <td><input type="text" class="regular-text" name="<?= $cat->slug ?>" value="<?= get_user_meta($user->ID,$cat->slug,true) ?>"></td>        </tr>        <?php endforeach;?>       <tr>         <th>Account Balance</th>              <td><input type="text" name="balance" class="regular-text" value="<?= get_user_meta($user->ID,'balance',true) ?>"></td>       </tr>       <tr>     